package com.molo.Utils;

import org.apache.log4j.Logger;

import com.molo.Utils.StringUtil;
import com.molo.Utils.BASE64Coder;
import com.molo.Utils.DESedeCoder;

public class EncodeUtil {
	
	private static final Logger log = Logger.getLogger(EncodeUtil.class); 

	/**
	 * 解密
	 * @param strByDesBase64
	 * @return
	 */
	public static String dencype(String strByDesBase64) {
    	String str = null;
		try {
			byte[] byteDes = new BASE64Coder().myDecrypt(strByDesBase64);
			byte[] bytes = new DESedeCoder().myDecrypt(byteDes, DESedeCoder.initKeyFroTwc);

			str = new String(bytes,"utf-8");

		} catch (Exception e1) {
			log.error("EncodeUtil.dencype.error:",e1);
		}
		return str;
    }
	  
	/**
	 * 加密 
	 * @param str
	 * @return
	 */
	public static String encype(String str) {
	 	String strdesbase64 = null;
	 	try {
				byte[] bytes = new DESedeCoder().myEncrypt(StringUtil.getUTF8Bytes(str), DESedeCoder.initKeyFroTwc);
				strdesbase64 = new BASE64Coder().myEncrypt(bytes);
			} catch (Exception e) {
				log.error("EncodeUtil.encype.error:",e);
			}
			return strdesbase64;
    }
	
	
	public static void main(String args[]) {
		String str = "";
		System.out.println(dencype(str));
	}
}
